$('.work').isotope({
    filter: '*',
    masonry: {
        isFitWidth: true
    }
});

$('.work').isotope('shuffle'); // 

$('.filters a').click(function () {
    $('.filters .active').removeClass('active');
    $(this).addClass('active');

    var selector = $(this).attr('data-filter');

    $('.work').isotope({
        filter: selector,
        masonry: {
            isFitWidth: true
        },
        animationOptions: {
            duration: 1000,
            easing: 'linear',
            queue: false
        }
    });

    $('.work').isotope('shuffle'); // 

    return false;
});

